import React, {useState, useRef} from 'react'
import './PostShare.css'
import ProfileImage from '../../img/profileImg.jpg'
// The below are icons
import { UilScenery } from "@iconscout/react-unicons";
import { UilPlayCircle } from "@iconscout/react-unicons";
import { UilLocationPoint } from "@iconscout/react-unicons";
import { UilSchedule } from "@iconscout/react-unicons";
import { UilTimes } from "@iconscout/react-unicons";


const PostShare = () => {
  const [image, setImage] = useState(null);
  const imageRef = useRef();
  const onImageChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      // the if condition checks if an image folder exists and if an image file exists
      //inside the folder specified, the image file is the image
      //we will upload using our input tag below
      let img = event.target.files[0];
      setImage({
        image: URL.createObjectURL(img),
        // our image variable from the usestate has a value of an object with
      });
    }
  };

  return (
    <div className="PostShare">
        <img src={ProfileImage} alt="" />
        <div>
          <input type="text" placeholder="What's happening"/>
          <div className="postOptions">
              <div className="option" style={{color: "var(--photo)"}} onClick={()=>imageRef.current.click()}>
                <UilScenery/>
                Photo
              </div>
              <div className="option" style={{color: "var(--video)"}}>
                <UilPlayCircle/>
                Video
              </div>
              <div className="option" style={{color: "var(--location)"}}>
                <UilLocationPoint/>
                Location
              </div>
              <div className="option" style={{color: "var(--sc hedule)"}}>
                <UilSchedule/>
                Schedule
              </div>
              <button className="button ps-button">
                Share
              </button>
              <div style={{display: "none"}}>
                <input type="file" name="myImage" ref={imageRef} onChange={onImageChange}/>
              </div>

          </div>

                  {image && (
                    <div className="previewImage">
                      // <UilTimes onClick={()=>setImage(null)}/>
                      <img src={image.image} alt="" />
                    </div>

                  )}
        </div>

    </div>
  )
}

export default PostShare
